    private void jMyButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMyButton1ActionPerformed
       
       String str="select stu_name,c_name,degree,mark from degree,student,course where student.stu_id=degree.stu_id and degree.stu_id like '%"+tsearch.getText()+"%'";
       res.getCustomRows(str, tblstudent);
    }//GEN-LAST:event_jMyButton1ActionPerformed

